Scripts.OnEnter:
if (not self.disabled) then
	local textOb = _G[self:GetName().."Text"];
	textOb:SetTextColor(1.0, 1.0, 1.0);
end

Scripts.OnLeave:
if (not self.disabled) then
	local textOb = _G[self:GetName().."Text"];

	if (textOb.color) then
		textOb:SetTextColor(textOb.color.r, textOb.color.g, textOb.color.b, textOb.color.a);
	else
		textOb:SetTextColor(1.0, 0.82, 0);
	end
end

Scripts.OnLoad:
self.widget = MiDKP.Widget.DDList:Create("Class",self)
UIDropDownMenu_SetWidth(self,100);
UIDropDownMenu_SetButtonWidth(self,22);
self.sortType = "class";
self.asc = true

self.widget = MiDKP.Widget.DDList:Create("Class",self)
UIDropDownMenu_SetWidth(self,127);
UIDropDownMenu_SetButtonWidth(self,22);
self.sortType = "class";
self.asc = true

self.widget = MiDKP.Widget.DDList:Create("DKP",self)
UIDropDownMenu_SetWidth(self,127);
UIDropDownMenu_SetButtonWidth(self,22);
UIDropDownMenu_SetText(self,MiDKP.L.DKP_PAGE_TITLE)
self.sortType = "point";
self.asc = true

for _i = 1, 6 do
	_G[self:GetName().."Tab".._i]:SetText(MIDKP_TABNAMES[_i])
end
self:SetAttribute("UIPanelLayout-defined", true);
self:SetAttribute("UIPanelLayout-area", "center");
self:SetAttribute("UIPanelLayout-pushable", 0);
self:SetAttribute("UIPanelLayout-enabled", true);
self:SetAttribute("UIPanelLayout-whiledead", true);

self:SetBackdropBorderColor(TOOLTIP_DEFAULT_COLOR.r, TOOLTIP_DEFAULT_COLOR.g, TOOLTIP_DEFAULT_COLOR.b);
self:SetBackdropColor(TOOLTIP_DEFAULT_BACKGROUND_COLOR.r, TOOLTIP_DEFAULT_BACKGROUND_COLOR.g, TOOLTIP_DEFAULT_BACKGROUND_COLOR.b);
if MiDKP.Debug then
	_G[self:GetName().."InnerFrameVersionText"]:SetText(string.format(MiDKP.L.ADDON_VERSION_TEXT,"Build: "..GetAddOnMetadata("MiDKP","X-BuildNumber")))
else
	_G[self:GetName().."InnerFrameVersionText"]:SetText(string.format(MiDKP.L.ADDON_VERSION_TEXT,"Ver: "..GetAddOnMetadata("MiDKP","Version")))
end
for _type,_page in pairs(MiDKP.UI.Pages) do
	_page:Init(self,_type)
	_page:OnLoad()					
end

MiDKP.UI.TabGroup:OnInit()

Scripts.OnTextChanged:
if (self.text) then
	self:SetText(self.text);
end
self:SetFocus();
self:HighlightText();

Scripts.OnCursorChanged:
if (self.text) then
	self:SetText(self.text);
end
self:SetFocus();
self:HighlightText();

Scripts.OnShow:
if (self.text) then
	self:SetText(self.text);
end
self:SetFocus();
self:HighlightText();
